import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = "http://localhost:5000/api";

const ManageCars = () => {
    const [vehicles, setVehicles] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [searchParams, setSearchParams] = useState({ type: '', startDate: '', endDate: '' });
    const [bookingResponse, setBookingResponse] = useState(null);
    const [newVehicle, setNewVehicle] = useState({
    model: '',
    licensePlate: '',
    price: '',
    category: 'Economy',
    status: 'Available',
    features: [],
    year: new Date().getFullYear(),
    transmission: 'Automatic',
    fuelType: 'Petrol',
    seats: 4
});

// Update the render method to include new fields
return (
    <div className="add-vehicle-form">
        <input
            type="text"
            name="model"
            placeholder="Model"
            value={newVehicle.model}
            onChange={(e) => setNewVehicle({...newVehicle, model: e.target.value})}
        />
        <input
            type="text"
            name="licensePlate"
            placeholder="License Plate"
            value={newVehicle.licensePlate}
            onChange={(e) => setNewVehicle({...newVehicle, licensePlate: e.target.value})}
        />
        <input
            type="number"
            name="price"
            placeholder="Price per day"
            value={newVehicle.price}
            onChange={(e) => setNewVehicle({...newVehicle, price: e.target.value})}
        />
        <select
            name="category"
            value={newVehicle.category}
            onChange={(e) => setNewVehicle({...newVehicle, category: e.target.value})}
        >
            <option value="Economy">Economy</option>
            <option value="Luxury">Luxury</option>
            <option value="SUV">SUV</option>
            <option value="Van">Van</option>
        </select>
        <input
            type="number"
            name="year"
            placeholder="Year"
            value={newVehicle.year}
            onChange={(e) => setNewVehicle({...newVehicle, year: e.target.value})}
        />
        <select
            name="transmission"
            value={newVehicle.transmission}
            onChange={(e) => setNewVehicle({...newVehicle, transmission: e.target.value})}
        >
            <option value="Automatic">Automatic</option>
            <option value="Manual">Manual</option>
        </select>
        <select
            name="fuelType"
            value={newVehicle.fuelType}
            onChange={(e) => setNewVehicle({...newVehicle, fuelType: e.target.value})}
        >
            <option value="Petrol">Petrol</option>
            <option value="Diesel">Diesel</option>
            <option value="Electric">Electric</option>
            <option value="Hybrid">Hybrid</option>
        </select>
        <input
            type="number"
            name="seats"
            placeholder="Number of seats"
            value={newVehicle.seats}
            onChange={(e) => setNewVehicle({...newVehicle, seats: e.target.value})}
        />
        <button onClick={handleAddVehicle}>Add Vehicle</button>
    </div>
);

    const getAuthHeader = () => {
        const token = localStorage.getItem('token');
        return {
            headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json'
            }
        };
    };

    useEffect(() => {
        fetchVehicles();
    }, []);

    const fetchVehicles = async () => {
        try {
            setLoading(true);
            const response = await axios.get(`${API_URL}/vehicles`, getAuthHeader());
            setVehicles(response.data);
            setError(null);
        } catch (error) {
            setError(error.response?.data?.message || 'Error fetching vehicles');
            console.error('Error fetching vehicles:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleAddVehicle = async () => {
        try {
            setLoading(true);
            const response = await axios.post(
                `${API_URL}/vehicles`,
                newVehicle,
                getAuthHeader()
            );
            setVehicles([...vehicles, response.data.vehicle]);
            setNewVehicle({
                model: '',
                licensePlate: '',
                price: '',
                category: 'Economy',
                status: 'Available'
            });
            setError(null);
        } catch (error) {
            setError(error.response?.data?.message || 'Error adding vehicle');
        } finally {
            setLoading(false);
        }
    };

    if (loading) return <div>Loading...</div>;

    return (
        <div className="manage-cars">
            <h2>Add New Vehicle</h2>
            {error && <div className="error-message">{error}</div>}

            <div className="add-vehicle-form">
                <input
                    type="text"
                    name="model"
                    placeholder="Model"
                    value={newVehicle.model}
                    onChange={(e) => setNewVehicle({...newVehicle, model: e.target.value})}
                />
                <input
                    type="text"
                    name="licensePlate"
                    placeholder="License Plate"
                    value={newVehicle.licensePlate}
                    onChange={(e) => setNewVehicle({...newVehicle, licensePlate: e.target.value})}
                />
                <input
                    type="number"
                    name="price"
                    placeholder="Price per day"
                    value={newVehicle.price}
                    onChange={(e) => setNewVehicle({...newVehicle, price: e.target.value})}
                />
                <select
                    name="category"
                    value={newVehicle.category}
                    onChange={(e) => setNewVehicle({...newVehicle, category: e.target.value})}
                >
                    <option value="Economy">Economy</option>
                    <option value="Luxury">Luxury</option>
                    <option value="SUV">SUV</option>
                    <option value="Van">Van</option>
                </select>
                <button onClick={handleAddVehicle}>Add Vehicle</button>
            </div>

            <h2>Vehicle List</h2>
            <div className="vehicles-list">
                {vehicles.map(vehicle => (
                    <div key={vehicle._id} className="vehicle-card">
                        <h3>{vehicle.model}</h3>
                        <p>License Plate: {vehicle.licensePlate}</p>
                        <p>Price: ${vehicle.price}/day</p>
                        <p>Status: {vehicle.status}</p>
                        <p>Category: {vehicle.category}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ManageCars;
