import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ManageCars = () => {
    const [vehicles, setVehicles] = useState([]);
    const [searchParams, setSearchParams] = useState({ type: '', startDate: '', endDate: '' });
    const [bookingResponse, setBookingResponse] = useState(null);
    const [newVehicle, setNewVehicle] = useState({ model: '', licensePlate: '', price: '', status: 'Available' });

    useEffect(() => {
        const fetchVehicles = async () => {
            try {
                const response = await axios.get('/api/vehicles');
                setVehicles(response.data);
            } catch (error) {
                console.error('Error fetching vehicles:', error);
            }
        };

        fetchVehicles();
    }, []);

    const handleSearchChange = (e) => {
        setSearchParams({ ...searchParams, [e.target.name]: e.target.value });
    };

    const handleSearch = async () => {
        try {
            const response = await axios.get('/api/vehicles', { params: searchParams });
            setVehicles(response.data);
        } catch (error) {
            console.error('Error searching vehicles:', error);
        }
    };

    const handleBooking = async (vehicleId) => {
        try {
            const response = await axios.post('/api/rentals/book', {
                vehicleId,
                startDate: searchParams.startDate,
                endDate: searchParams.endDate
            });
            setBookingResponse(response.data);
        } catch (error) {
            console.error('Error booking vehicle:', error);
        }
    };

    const handleAddVehicleChange = (e) => {
        setNewVehicle({ ...newVehicle, [e.target.name]: e.target.value });
    };

    const handleAddVehicle = async () => {
        try {
            const response = await axios.post('/api/vehicles', newVehicle);
            setVehicles([...vehicles, response.data.vehicle]);
            setNewVehicle({ model: '', licensePlate: '', price: '', status: 'Available' }); // Reset form
        } catch (error) {
            console.error('Error adding vehicle:', error);
        }
    };

    return (
        <div>
            <h1>Manage Cars</h1>
            <h2>Add New Vehicle</h2>
            <input
                type="text"
                name="model"
                placeholder="Model"
                value={newVehicle.model}
                onChange={handleAddVehicleChange}
            />
            <input
                type="text"
                name="licensePlate"
                placeholder="License Plate"
                value={newVehicle.licensePlate}
                onChange={handleAddVehicleChange}
            />
            <input
                type="number"
                name="price"
                placeholder="Price"
                value={newVehicle.price}
                onChange={handleAddVehicleChange}
            />
            <button onClick={handleAddVehicle}>Add Vehicle</button>

            <h2>Search Vehicles</h2>
            <input
                type="text"
                name="type"
                placeholder="Vehicle Type"
                value={searchParams.type}
                onChange={handleSearchChange}
            />
            <input
                type="date"
                name="startDate"
                value={searchParams.startDate}
                onChange={handleSearchChange}
            />
            <input
                type="date"
                name="endDate"
                value={searchParams.endDate}
                onChange={handleSearchChange}
            />
            <button onClick={handleSearch}>Search</button>

            <ul>
                {vehicles.map(vehicle => (
                    <li key={vehicle._id}>
                        {vehicle.model} - {vehicle.status}
                        <button onClick={() => handleBooking(vehicle._id)}>Book</button>
                    </li>
                ))}
            </ul>

            {bookingResponse && <div>Booking Successful: {JSON.stringify(bookingResponse)}</div>}
        </div>
    );
};

export default ManageCars;
